function binarySearch(element,list){
    var output,originalList=list;
    if (!list.includes(element)) return "Element not found";
    while (output==null){
        var halfLen=Math.floor(list.length/2);
        list1=list.slice(0,halfLen);
        list2=list.slice(halfLen,list.length);
        if (!list1.includes(element)) list=list2;
        if (!list2.includes(element)) list=list1;
        else output=originalList.indexOf(element);   
    }
    return output;
}