function difference(list1,list2) {
    var temp = [];
    list1 = list1.toString().split(',').map(Number);
    list2 = list2.toString().split(',').map(Number);
    for (var i in list1) {
        if(list2.indexOf(list1[i]) === -1) temp.push(list1[i]);
    }
    for(j in list2) {
        if(list1.indexOf(list2[j]) === -1) temp.push(list2[j]);
    }
    return temp.sort((a,b) => a-b);
}