function mostFrequent(list){
    var distinctElement=[],occurrence=[];
    for(var i=0;i<list.length;i++){
        if (!distinctElement.includes(list[i])) distinctElement.push(list[i]);
    }
    
    for (var j=0;j<distinctElement.length;j++){
        occurrence.push(count(distinctElement[j],list));
    }
    var max = occurrence.reduce(function(a, b) {
        return Math.max(a, b);
    });
    return distinctElement[occurrence.indexOf(max)]+": "+max+" times.";
}