function swapCase(string){
    var stringList=string.split(" ");
    for (var i=0;i<stringList.length;i++){
        for (var j=0;j<stringList[i].length;j++){
            if (stringList[i].charAt(j)!=stringList[i].charAt(j).toUpperCase()) stringList[i]=stringList[i].replace(stringList[i].charAt(j),stringList[i].charAt(j).toUpperCase());
            else stringList[i]=stringList[i].replace(stringList[i].charAt(j),stringList[i].charAt(j).toLowerCase());
        }
    }
    return stringList.join(' ');
}
console.log(swapCase("The QuICk BroWn FoX"));
