function leap(year){
    return year%4==0 & year%100!=0 || year%400==0;
}
function daysToChristmas(){
    var today= new Date(), todayNum=0,christmasNum=0;
    var christmas= new Date(today.getFullYear(),11,25);
    var month=[31,28,31,30,31,30,31,31,30,31,30];
    var monthLeap=[31,29,31,30,31,30,31,31,30,31,30];
    if (leap(today.getFullYear())) month = monthLeap;
    for (var i=0;i<today.getMonth();i++){
        todayNum+=month[i];
    }
    todayNum+= today.getDate();
    for (var j=0;j<11;j++){
        christmasNum+=month[i];
    }
    christmasNum+=25;
    return christmasNum-todayNum;
}
console.log(daysToChristmas());