function sumEqualsSum(list){
    var testList=[];
    for (var i = 0; i<list.length;i++){
        for (var j = i; j<list.length;j++){
            if (i !== j){
                testList.push(list[i]+list[j]);
            }
        }
    }
    for (var m = 0; m<testList.length;m++){
        for (var n = 0; n<testList.length;n++){
            if (testList[m] === testList[n] & m !== n ){ return 1;}
        }
    }
    return 0;
}