function happyNumber(num) {
    var m, n,c= [] ;
    while(num != 1 && c[num] !== true) {
        c[num] = true ;
        m = 0 ;
        while (num > 0) {
            n = num % 10 ;
            m += n * n ;
            num = (num  - n) / 10 ;
        }
        num = m ;
    }
    return (num == 1) ;
}