function dayDifference(date1, date2) {
    theDay1 = new Date(date1);
    theDay2 = new Date(date2);
    return Math.floor((Date.UTC(theDay2.getFullYear(), theDay2.getMonth(), theDay2.getDate()) - Date.UTC(theDay1.getFullYear(), theDay1.getMonth(), theDay1.getDate())) / (1000 * 60 * 60 * 24));
}
console.log(dayDifference('04/02/2014', '11/04/2014')); 