function hourDifference(theDay2, theDay1) {
    var diff = (theDay2.getTime() - theDay1.getTime()) / 1000;
    diff /= (60 * 60);
    return Math.abs(Math.round(diff));
}
theDay1 = new Date("October 13, 2014 08:11:00"); 
theDay2 = new Date("October 13, 2014 11:13:00"); 
console.log(hourDifference(theDay1, theDay2)); 