function maxDate(listDates) {
    var maxDay = listDates[0], maxDayObj = new Date(listDates[0]);
    listDates.forEach(function (theDay, index) {
        if (new Date(theDay) > maxDayObj) {
            maxDay = theDay;
            maxDayObj = new Date(theDay);
        }
    });
    return maxDay;
}
console.log(maxDate(['2015/02/01', '2015/02/02', '2015/01/03']));