function charCount(string){
    var uniqueStr= uniqueChar(string),charOccurrence=[],stringList=string.split(""),output="";
    for (i=0;i<uniqueStr.length;i++){
        charOccurrence.push(count(uniqueStr[i],stringList)); 
        if (i==uniqueStr.length-1)  output+=uniqueStr[i]+": "+charOccurrence[i];
        else output+=uniqueStr[i]+": "+charOccurrence[i]+"\n";
    }
    return output;
}