function longestString(string){
    var stringList=string.split(" "),stringLen=[];
    for (var i=0;i<stringList.length;i++){
        stringLen.push(stringList[i].length);
    }
    var max = stringLen.reduce(function(a, b) {
        return Math.max(a, b);
    });
    return stringList[stringLen.indexOf(max)];
}
