function stringUpperCase(string){
    var stringList=string.split(" ");
    for (var i=0;i<stringList.length;i++){
        stringList[i]=stringList[i].replace(stringList[i].charAt(0),stringList[i].charAt(0).toUpperCase());
    }
    return stringList.join(' ');
}
console.log(stringUpperCase("the quick brown fox"));