function vowel(string){
    var stringList=string.split(" "), vowelCount=0;
    var vowelList=['a','e','i','o','u'];
    for (var i=0;i<stringList.length;i++){
        for (var j=0;j<stringList[i].length;j++){
            if (vowelList.includes(stringList[i].charAt(j))) vowelCount++;
        }
    }
    return vowelCount;
}
console.log(vowel('dcm gdzgdfzsfdgnfgaseffv'));