function pancakeSort(arr) {
    for (var i = arr.length - 1; i >= 1; i--) {
        var maxIdx = 0;
        var max = arr[0];
        for (var j = 1; j <= i; j++) {
            if (arr[j] > max) {
                max = arr[j];
                maxIdx = j;
            }
        }
        if (maxIdx == i) 
            continue; 
        var newSlice;
        if (maxIdx > 0) {
            newSlice = arr.slice(0, maxIdx+1).reverse();
            for ( j = 0; j <= maxIdx; j++) 
                arr[j] = newSlice[j];
        }
        newSlice = arr.slice(0, i+1).reverse();
        for ( j = 0; j <= i; j++) 
            arr[j] = newSlice[j];
    }
    return arr;
}
console.log(pancakeSort([-11,45,6,8,-3,-9,17,34,2,-32]));